import { HeaderDetails } from './services/header';
import { HeaderService } from './services/header.service';

@Component({
  selector: 'portfolio-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

  headerInformation : HeaderDetails = this.headerService.headerInformation;

  constructor (private headerService: HeaderService) {

  }

  ngOnInit () {
  }

}
<div class="introDiv container-fluid w-100 py-5 ps-5">
      <div class="headerTextDiv px-1 mx-auto my-5 text-center">
        <h1 class="headerLine1  text-primary-emphasis py-5">{{ headerInformation.heading }}</h1>
        <h3 class="headerTextGroup headerLine3  display-6 text-primary-emphasis">
          {{ headerInformation.subheader }}
        </h3>
        <h2 class="headerTextGroup headerLine3 col-lg-6 lead my-3 text-muted mx-auto">{{ headerInformation.introduction }}</h2>
